mapper.select:
SELECT tg.ID as ID, tg.NAME as NAME, tg.DESCRIPTION as DESCRIPTION, tg.PRICE as PRICE, tg.VIP_PRICE as VIP_PRICE, tg.STOCK as STOCK, tg.SHOP_ID as SHOP_ID, ( SELECT URL FROM GOODS_IMAGE WHERE OWNER_GOODS_ID = tg.ID AND DELETED = 0 LIMIT 1 ) as IMG_URL FROM ( SELECT ID, NAME, DESCRIPTION, PRICE, VIP_PRICE, STOCK, SHOP_ID, CREATED_AT FROM GOODS WHERE
name LIKE '%' #{name} '%' AND
STATUS != 'deleted' ) tg ORDER BY tg.CREATED_AT DESC LIMIT #{start},#{offset}

SELECT COUNT(*)
FROM GOODS
WHERE STATUS != 'deleted' AND  `NAME` LIKE '%' #{name} '%';
