mapper.update:
UPDATE ORDER_TABLE
SET
UPDATED_AT = now()

    ,
    EXPRESS_COMPANY = #{expressCompany}


    ,
    EXPRESS_ID = #{expressId}


    ,
    STATUS = #{status}

WHERE
ID = #{id}

mapper.select:
select * from ORDER_TABLE
WHERE

    STATUS = #{status} AND


    STATUS != 'deleted'


    AND USER_ID = #{userId}


   AND PICKUP_TYPE = #{pickupType}


ORDER BY CREATED_AT DESC
limit #{start}, #{offset}
